
$(document).ready(function(e) {
	creatLoginFun();
	
	$('#login').click(function(e) {
		$('#loginDialog').fadeIn();
	});
});


function creatLoginFun(){
	var loginHtml = '<!--登录注册弹窗-->'+
					'<div class="opacitybg" id="loginDialog" style="display:none;">'+
						'<div class="login bg-white">'+
							'<a class="login-close" id="loginClose"></a>'+
							'<!--快速登录-->'+
							'<div class="fast-type" id="FastType" style="display:block;">'+
								'<h3 class="fs20 fc-black100">手机号快速登录</h3>'+
								'<p class="fs14 fc-black50">未注册过的手机号将自动创建账号</p>'+
								'<div class="phone-num mt16"><input class="fs14 fc-black100" type="text" placeholder="请输入手机号" /></div>'+
								'<div class="phone-checkcode mt16"><input class="fs14 fc-black100" type="text" placeholder="请输入验证码" /><input class="ml8 fs14 fc-black100 " type="button" value="获取验证码" /></div>'+
								'<div class="accept-service mt16 fs12 fc-black50">'+
									'<input type="checkbox" checked id="acceptService">'+
									'<label for="acceptService">我已经看过并同意《问房海外服务协议》</label>'+
								'</div>'+
								'<a class="login-btn bg-blue100 fc-white fs14 mt24">登录</a>'+
								'<span class="orther-type fs14 fc-blue100 mt40" id="changeNormalType">账号密码登录</span>'+
							'</div>'+
							'<!--账号登录-->'+
							'<div class="normal-type" id="NormalType" style="display:none;">'+
								'<h3 class="fs20 fc-black100">账号密码登录</h3>'+
								'<p class="fs14 fc-black50">忘记密码可通过手机号快速登录后进入个人中心修改密码</p>'+
								'<div class="phone-num mt16"><input class="fs14 fc-black100" type="text" placeholder="请输入手机号" /></div>'+
								'<div class="password mt16"><input class="fs14 fc-black100" type="text" placeholder="请输入登录密码" /></div>'+
								'<div class="accept-service mt16 fs12 fc-black50">'+
									'<input type="checkbox" checked id="acceptService">'+
									'<label for="acceptService">我已经看过并同意《问房海外服务协议》</label>'+
								'</div>'+
								'<a class="login-btn bg-blue100 fc-white fs14 mt24">登录</a>'+
								'<span class="orther-type fs14 fc-blue100 mt40" id="changeFastType">手机号快速登录</span>'+
							'</div>'+
							
							'<!--设置密码-->'+
							'<div class="set-password" style="display:none;">'+
								'<h3 class="fs20 fc-black100">设置登录密码</h3>'+
								'<p class="fs14 fc-black50">设置登录密码后可直接通过手机号和登录密码登录</p>'+
								'<div class="password mt16"><input class="fs14 fc-black100" type="text" placeholder="请输入6-16位字符的登录密码" /></div>'+
								'<div class="post-skip mt24 fs16">'+
									'<a class="skipbtn fc-black50">跳过</a><a class="postbtn fc-blue100">确定</a>'+
								'</div>'+
							'</div>'+
						'</div>'+
					'</div>';
	
	
	//自动生成登录弹框布局
	$('body').append(loginHtml);

	//手机号快速登录和账号密码登录快速切换
	$('#changeNormalType').click(function(e) {
		$('div#FastType').hide().siblings('div#NormalType').show();
	});
	$('#changeFastType').click(function(e) {
		$('div#NormalType').hide().siblings('div#FastType').show();
	});
	
	//关闭登录
	$('#loginClose').click(function(e) {
		$('div#loginDialog').fadeOut();
	});
}